SELECT
o.order_id, o.user_id, o.room_id, r.room_number, o.order_num, o.reserve_date,
o.checkin_date, o.checkout_date, o.status, o.payment, o.create_time, o.update_time
FROM hotel_order o
LEFT JOIN hotel_room r ON o.room_id = r.room_id


        AND o.user_id = #{userId}


        AND o.room_id = #{roomId}


        AND r.room_number LIKE CONCAT('%', #{roomNumber}, '%')


        AND o.order_num LIKE CONCAT('%', #{orderNum}, '%')


        AND o.status = #{status}


        AND o.reserve_date = #{reserveDate}


        AND o.checkin_date = #{checkinDate}


        AND o.checkout_date = #{checkoutDate}

SELECT *
FROM hotel_order
WHERE checkin_date BETWEEN #{startDate} AND #{endDate}
   OR checkout_date BETWEEN #{startDate} AND #{endDate}
   OR (checkin_date <= #{startDate} AND checkout_date >= #{endDate})

SELECT *
FROM hotel_order
WHERE checkin_date <= #{date}
  AND checkout_date >= #{date}

SELECT o.order_id, o.user_id, u.username,u.phone, u.gender,o.room_id, r.room_number, t.type_name,o.order_num,o.checkin_date, o.checkout_date, o.payment FROM hotel_order o LEFT JOIN sys_user u ON o.user_id = u.user_id LEFT JOIN hotel_room r ON o.room_id = r.room_id LEFT JOIN hotel_room_type t ON r.room_type_id = t.room_type_id
o.status = #{status}
AND r.room_number LIKE CONCAT('%', #{roomNumber}, '%')
AND o.order_num LIKE CONCAT('%', #{orderNum}, '%')
AND o.status = #{status}
AND o.checkin_date = #{checkinDate}
AND o.checkout_date = #{checkoutDate}

SELECT o.order_id, o.user_id, o.room_id, o.order_num, o.reserve_date, o.checkin_date, o.checkout_date, o.status, o.payment, o.create_time, o.update_time, u.username, u.phone, u.gender, r.room_number,rt.type_name, DATEDIFF(o.checkout_date, o.checkin_date) AS days FROM hotel_order o LEFT JOIN sys_user u ON o.user_id = u.user_id LEFT JOIN hotel_room r ON o.room_id = r.room_id LEFT JOIN hotel_room_type rt ON r.room_type_id = rt.room_type_id
AND o.reserve_date = #{reserveDate}
AND o.checkin_date = #{checkinDate}
AND o.checkout_date = #{checkoutDate}
AND o.status = #{status}
AND o.order_num LIKE CONCAT('%', #{orderNum}, '%')
AND u.username LIKE CONCAT('%', #{username}, '%')
AND u.phone LIKE CONCAT('%', #{phone}, '%')
AND r.room_number LIKE CONCAT('%', #{roomNumber}, '%')
AND o.create_time >= #{startTime}
AND o.create_time <= #{endTime}
